mapper.select:
select *
from blogcontents
where blog_id = #{blogId} and blog_contents_id = #{blogContentsId}

mapper.update:
update blogcontents
set blog_contents = #{blogContents}, blog_contents_title = #{blogContentsTitle}, blog_contents_cover = #{blogContentsCover}
where blog_id = #{blogId} and blog_contents_id = #{blogContentsId}
