mapper.select:
select uid, nickname, username, profile
from userinfo
where uid = #{uid}

select blog_id, blog_title, blog_like, blog_cover, blog_view
from blog
where blog_id = #{blogId}

mapper.update:
update userinfo
set nickname = #{nickname}
where uid = #{uid}

update user
set isdeleted = 1
where uid = #{uid}
